history.query:
query Search($term: String!) {
  search(matching: $term) {
    ...MenuItemFields
    ...CategoryFields
  }
}

fragment MenuItemFields on MenuItem {
  name
  price
  description
}

fragment CategoryFields on Category {
  name
  items {
    ...MenuItemFields
  }
}

